import styled from "styled-components";

const HeroImage = styled.img`
  height: 16em;
  width: 16em;
  object-fit: contain;
  border-radius: 50%;
`;

const AboutContainer = styled.div`
  background-color: rgba(225, 225, 225, 0.8);
  box-sizing: border-box;
  padding: 10em 5em 10em 0em;
  width: 100%;
  margin: auto;
  display: flex;
  text-align: center;
`;

const AboutText = styled.div`
  margin: auto;
  text-align: center;
  padding: 2em 5em;
`;

const About = () => {
  return (
    <AboutContainer>
      <HeroImage src="/profile-photo.jpg" alt="profile-photo" />
      <AboutText>
        <h1>hi there...</h1>
        <h4>
          Nice to meet you. I'm Hal, a personable frontend developer with 1+
          year's experience building websites and applications. Below you'll
          find examples of my work - please get in touch if you like what you
          see!
        </h4>
      </AboutText>
    </AboutContainer>
  );
};

export default About;
