// make Project titles in line with project descriptions.

import styled from "styled-components";
import nbrly from "../images/nbrly.png";
import weatherApp from "../images/weather-app.png";
import nasa from "../images/nasa.png";

const AllProjectsContainer = styled.div`
  // media query here for mobile version to change flex-direction to row.

  // display: flex;
  // flex-direction: column;
  // justify-content: center;
  // align-items: items;
  margin: 1em;
  padding: 0 4em 6em 4em;

  // @media (max-width: 768px) {
  //   flex-direction: column;
  //   padding: 10em 2em;
  //   align-items: center;
  // }
`;

const SingleJobContainer = styled.span`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: items;
  padding: 0em 2em 2em 2em;

  @media (max-width: 768px) {
    flex-direction: column;
    padding: 2em 0;
    align-items: center;
  }
`;

const ProjectImg = styled.img`
  width: 300px;
  height: 100%;
`;

const Projects = () => {
  return (
    <AllProjectsContainer>
      <h3 style={{ textAlign: "center" }}>NBRLY - currently in construction</h3>
      <SingleJobContainer>
        <ProjectImg img src={nbrly} />

        <p>
          A full stack web app that connects you with the people around you. By
          answering the simple question, "What do you want to do today?" you are
          instantly linked with like-minded people in your geographic vicinity
          for sports, nightlife, and everything in between. Built with React and
          node, in collaboration with developers Robin Edwards and Anna Balquin.
          Click here to view code.
        </p>
      </SingleJobContainer>

      <h3 style={{ textAlign: "center" }}>NASA Search Engine</h3>
      <SingleJobContainer>
        <ProjectImg img src={nasa} />

        <p>
          A search engine app for NASA's image library. API endpoint was
          provided as part of the brief. User is able to search for and return
          relevant images. Built with React. Click{` `}
          <a href="https://halfulcher.github.io/nasa-app/">here</a> to view
          deployed app. Click{` `}
          <a href="https://github.com/HalFulcher/nasa-app">here</a> to view
          code.
        </p>
      </SingleJobContainer>
      <h3 style={{ textAlign: "center" }}>Weather App</h3>
      <SingleJobContainer>
        <ProjectImg img src={weatherApp} />

        <p>
          The app uses the Open Weather API to retrieve and display 5-day
          weather forecast data for towns and cities in the UK. It includes
          detailed information such as: min and max temperature, humidity, wind
          direction and speed as well as a general description and icon
          depicting the general forecast. Made with React. Click here to view
          code. Click here to view deployed app.
        </p>
      </SingleJobContainer>
    </AllProjectsContainer>
  );
};

export default Projects;
